SELECT
  ?ligand
  ?ligandName 
  (COUNT(DISTINCT ?protein) as ?entries)
WHERE {
   ?protein up:annotation ?annotation .

   VALUES ?evs { obo:ECO_0000269 obo:ECO_0007744 } .
   VALUES ?chebids { CHEBI:25213 CHEBI:25214 } .
   ?st rdf:subject ?protein ; 
       rdf:predicate up:annotation ; 
       rdf:object ?annotation ;
       up:attribution/up:evidence ?evs .
   ?annotation up:ligand/rdfs:subClassOf ?ligand .
   ?ligand rdfs:subClassOf+ ?chebids ;
     rdfs:label ?ligandName .
}
GROUP BY ?ligand ?ligandName
ORDER BY DESC(?entries)

initial_sparql:
SELECT (COUNT(DISTINCT ?protein) as ?count)
WHERE {
    ?protein a up:Protein ;
            up:annotation ?annotation .
    ?annotation a up:Binding_Site_Annotation ;
               rdfs:comment ?comment .

    # Link to evidence
    [] rdf:subject ?annotation ;
       up:attribution ?attribution .
    ?attribution up:evidence <http://purl.obolibrary.org/obo/ECO_0000269> .

    # Look for metal-related binding sites
    FILTER(REGEX(?comment, "metal|zinc|iron|copper|magnesium|calcium|manganese|cobalt|nickel|molybdenum|tungsten|vanadium|cluster", "i"))
}

improved_sparql:
SELECT (COUNT(DISTINCT ?protein) as ?count)
WHERE {
    ?protein a up:Protein ;
            up:annotation ?annotation .
    ?annotation a up:Binding_Site_Annotation ;
               rdfs:comment ?comment ;
               up:range ?range .

    # Direct link to evidence
    ?annotation up:attribution ?attribution .
    ?attribution up:evidence ?evidence .
    FILTER(?evidence IN (<http://purl.obolibrary.org/obo/ECO_0000269>, <http://purl.obolibrary.org/obo/ECO_0000314>))

    # Comprehensive metal and metal-sulfur cluster pattern
    FILTER(REGEX(DISTINCT(?comment), 
                 "metal|zinc|iron|copper|magnesium|calcium|manganese|cobalt|nickel|molybdenum|tungsten|vanadium|silver|platinum|chromium|selenium|Fe-S|iron-sulfur|metal-sulfur|cluster", 
                 "i"))
}
